// ATT System - Deep Competitor Agent #06 - PRODUCTION
// Real ScrapeHero Product API deep fetch on competitor ASINs

const { trackCost } = require('../../shared/cost-tracker');
const { isDuplicateTopic } = require('../../shared/utils');
const { errorHandler } = require('../../shared/error-handler');
const { ScrapeHeroClient } = require('../../clients/scrapehero-client');

async function deepFetchCompetitor(asin, client) {
  console.log(`ðŸ” Real deep fetch for ASIN: ${asin}`);

  const productResult = await client.scrapeAmazonProduct(asin, {
    marketplace: 'US',
    includeReviews: true,
    maxReviews: 20,
    includeVariants: true
  });

  if (!productResult.success) {
    console.warn(`âš ï¸ Failed to fetch ${asin}: ${productResult.error}`);
    return null;
  }

  const product = productResult.product;

  // Calculate listing quality score based on real data
  const qualityScore = calculateListingQuality(product);

  return {
    asin,
    fetch_timestamp: new Date().toISOString(),
    product_details: {
      title: product.title,
      brand: product.brand,
      price: product.price,
      original_price: product.originalPrice,
      discount: product.discount,
      bsr: product.bsr ? (Array.isArray(product.bsr) ? product.bsr[0].rank : product.bsr) : null,
      category_bsr: product.bsr ? (Array.isArray(product.bsr) ? product.bsr.find(b => b.category !== 'Main')?.rank : null) : null,
      reviews: {
        count: product.reviewCount,
        rating: product.rating,
        recent_reviews: product.reviews?.slice(0, 5) || []
      },
      images: product.images,
      features: product.features,
      description: product.description,
      variations: {
        colors: product.variants?.length || 0,
        available_variants: product.variants?.map(v => ({
          asin: v.asin,
          color: v.color,
          size: v.size,
          price: v.price
        })) || []
      },
      dimensions: product.dimensions,
      weight: product.weight,
      availability: product.availability,
      seller: product.seller,
      fulfillment: product.fulfillment,
      keywords: product.keywords,
      competitor_score: product.competitorScore,
      listing_quality_score: qualityScore,
      sales_estimate: estimateSales(product)
    },
    scrapehero_metadata: productResult.metadata
  };
}

function calculateListingQuality(product) {
  let score = 0;

  // Title quality (0-20 points)
  if (product.title && product.title.length > 50) score += 20;
  else if (product.title && product.title.length > 30) score += 15;
  else score += 5;

  // Images quality (0-15 points)
  const imageCount = product.images?.length || 0;
  score += Math.min(15, imageCount * 3);

  // Reviews (0-25 points)
  if (product.reviewCount > 1000) score += 25;
  else if (product.reviewCount > 100) score += 20;
  else if (product.reviewCount > 10) score += 15;
  else score += 5;

  // Rating (0-15 points)
  if (product.rating >= 4.5) score += 15;
  else if (product.rating >= 4.0) score += 12;
  else if (product.rating >= 3.5) score += 8;
  else score += 3;

  // Features/bullets (0-10 points)
  const featureCount = product.features?.length || 0;
  score += Math.min(10, featureCount * 2);

  // Description (0-10 points)
  if (product.description && product.description.length > 200) score += 10;
  else if (product.description && product.description.length > 100) score += 7;
  else score += 3;

  // BSR bonus (0-5 points)
  const bsr = Array.isArray(product.bsr) ? product.bsr[0]?.rank : product.bsr;
  if (bsr && bsr < 50000) score += 5;
  else if (bsr && bsr < 100000) score += 3;
  else if (bsr && bsr < 500000) score += 1;

  return Math.min(100, score);
}

function estimateSales(product) {
  const bsr = Array.isArray(product.bsr) ? product.bsr[0]?.rank : product.bsr;
  const reviewCount = product.reviewCount || 0;

  // Simple sales estimation based on BSR and reviews
  if (!bsr) return Math.max(1, Math.floor(reviewCount / 10));

  if (bsr < 10000) return Math.floor(reviewCount / 2) + 50;
  if (bsr < 50000) return Math.floor(reviewCount / 5) + 20;
  if (bsr < 100000) return Math.floor(reviewCount / 10) + 10;
  if (bsr < 500000) return Math.floor(reviewCount / 20) + 5;

  return Math.max(1, Math.floor(reviewCount / 50));
}

function analyzeMarketGaps(products) {
  const gaps = [];

  const avgImages = products.reduce((sum, p) => sum + (p.deep_data.images?.length || 0), 0) / products.length;
  const avgFeatures = products.reduce((sum, p) => sum + (p.deep_data.features?.length || 0), 0) / products.length;
  const avgVariants = products.reduce((sum, p) => sum + (p.deep_data.variations?.colors || 0), 0) / products.length;

  if (avgImages < 5) gaps.push('Limited product photography');
  if (avgFeatures < 4) gaps.push('Minimal feature descriptions');
  if (avgVariants < 3) gaps.push('Limited color/size variety');

  const hasLowQuality = products.some(p => p.deep_data.listing_quality_score < 70);
  if (hasLowQuality) gaps.push('Quality listing optimization opportunity');

  return gaps;
}

async function analyzeCompetitorGroup(competitorData) {
  const scrapeheroApiKey = process.env.SCRAPEHERO_API_KEY;

  if (!scrapeheroApiKey) {
    throw new Error('SCRAPEHERO_API_KEY environment variable not set');
  }

  const client = new ScrapeHeroClient(scrapeheroApiKey);
  const products = competitorData.serp_data?.top_competitors || [];
  const deepAnalysis = [];

  // Analyze top 3 competitors with real data
  for (const competitor of products.slice(0, 3)) {
    if (!competitor.asin || competitor.asin.startsWith('UNKNOWN')) {
      console.warn(`âš ï¸ Skipping invalid ASIN: ${competitor.asin}`);
      continue;
    }

    const productDetails = await deepFetchCompetitor(competitor.asin, client);

    if (productDetails) {
      const competitiveAdvantages = [
        productDetails.product_details.bsr ? `BSR: ${productDetails.product_details.bsr}` : 'BSR: Not available',
        `Reviews: ${productDetails.product_details.reviews.count}`,
        `Rating: ${productDetails.product_details.reviews.rating}/5`,
        `Quality: ${productDetails.product_details.listing_quality_score}/100`,
        `Variants: ${productDetails.product_details.variations.colors}`
      ];

      deepAnalysis.push({
        competitor_rank: competitor.rank,
        asin: competitor.asin,
        deep_data: productDetails.product_details,
        competitive_advantages: competitiveAdvantages,
        fetch_metadata: productDetails.scrapehero_metadata
      });

      // Rate limiting
      await new Promise(resolve => setTimeout(resolve, 1000));
    }
  }

  return deepAnalysis;
}

async function main() {
  const inputData = $input.first().json;
  const niche = inputData.niche;

  console.log(`ðŸ”¬ Real deep competitor analysis for: ${niche}`);

  try {
    // Track real ScrapeHero API cost (per product)
    await trackCost('scrapehero_deep', 0.005, `Agent #06 - Real deep competitor analysis for ${niche}`);

    const deepAnalysis = await analyzeCompetitorGroup(inputData);

    if (deepAnalysis.length === 0) {
      return [{ json: {
        niche,
        error: 'No valid competitor ASINs found for deep analysis',
        agent_source: 'deep-competitor-06',
        processed_at: new Date().toISOString()
      }}];
    }

    // Calculate aggregate insights from real data
    const validBSRs = deepAnalysis.filter(a => a.deep_data.bsr).map(a => a.deep_data.bsr);
    const avgBSR = validBSRs.length > 0 ? validBSRs.reduce((sum, bsr) => sum + bsr, 0) / validBSRs.length : null;
    const avgReviews = deepAnalysis.reduce((sum, a) => sum + a.deep_data.reviews.count, 0) / deepAnalysis.length;
    const avgRating = deepAnalysis.reduce((sum, a) => sum + a.deep_data.reviews.rating, 0) / deepAnalysis.length;
    const avgQuality = deepAnalysis.reduce((sum, a) => sum + a.deep_data.listing_quality_score, 0) / deepAnalysis.length;
    const totalSalesEstimate = deepAnalysis.reduce((sum, a) => sum + (a.deep_data.sales_estimate || 0), 0);

    const marketGaps = analyzeMarketGaps(deepAnalysis);

    const result = {
      niche,
      deep_competitor_analysis: deepAnalysis,
      aggregate_insights: {
        analyzed_count: deepAnalysis.length,
        average_bsr: avgBSR ? Math.round(avgBSR) : null,
        average_reviews: Math.round(avgReviews),
        average_rating: parseFloat(avgRating.toFixed(2)),
        average_quality_score: Math.round(avgQuality),
