async function analyzeCompetitorGroup(competitorData) {
  const products = competitorData.serp_data.top_competitors;
  const deepAnalysis = [];

  for (const competitor of products.slice(0, 3)) { // Analyze top 3
    const productDetails = await deepFetchCompetitor(competitor.asin);
    deepAnalysis.push({
      competitor_rank: competitor.rank,
      asin: competitor.asin,
      deep_data: productDetails.product_details,
      competitive_advantages: [
        `BSR: ${productDetails.product_details.bsr}`,
        `Reviews: ${productDetails.product_details.reviews.count}`,
        `Quality: ${productDetails.product_details.listing_quality_score}/100`
      ],
      market_gaps: [
        'Limited color variety',
        'Basic product photos',
        'Generic description'
      ]
    });
  }

  return deepAnalysis;
}

async function main() {
  const inputData = $input.first().json;
  const niche = inputData.niche;

