    // Calculate aggregate insights
    const avgBSR = deepAnalysis.reduce((sum, a) => sum + a.deep_data.bsr, 0) / deepAnalysis.length;
    const avgReviews = deepAnalysis.reduce((sum, a) => sum + a.deep_data.reviews.count, 0) / deepAnalysis.length;
    const avgQuality = deepAnalysis.reduce((sum, a) => sum + a.deep_data.listing_quality_score, 0) / deepAnalysis.length;

