// ATT System - Perplexity Synthesis Agent #07 - PRODUCTION
// Real Perplexity Sonar cultural insights synthesis

const { trackCost } = require('../../shared/cost-tracker');
const { isDuplicateTopic } = require('../../shared/utils');
const { errorHandler } = require('../../shared/error-handler');
const { PerplexityClient } = require('../../clients/perplexity-client');

async function synthesizeCulturalInsights(niche, competitorData) {
  const perplexityApiKey = process.env.PERPLEXITY_API_KEY;

  if (!perplexityApiKey) {
    throw new Error('PERPLEXITY_API_KEY environment variable not set');
  }

  const client = new PerplexityClient(perplexityApiKey);

  console.log(`üåç Real cultural analysis for: ${niche}`);

  // Get cultural insights from Perplexity Sonar
  const insightsResult = await client.getCulturalInsights(niche, {
    region: 'US',
    timeframe: '30d',
    depth: 'comprehensive',
    includeComparisons: true
  });

  if (!insightsResult.success) {
    throw new Error(`Perplexity cultural insights failed: ${insightsResult.error}`);
  }

  // Extract competitor keywords for trend analysis
  const competitorKeywords = [];
  if (competitorData.serp_data?.top_competitors) {
    competitorData.serp_data.top_competitors.forEach(comp => {
      if (comp.title) {
        // Extract keywords from competitor titles
        const titleWords = comp.title.toLowerCase()
          .replace(/[^a-zA-Z0-9\s]/g, '')
          .split(' ')
          .filter(word => word.length > 3 && !['shirt', 'tshirt', 'design', 'funny', 'cool'].includes(word));
        competitorKeywords.push(...titleWords.slice(0, 3));
      }
    });
  }

  // Get trending analysis for competitor keywords
  let trendData = null;
  if (competitorKeywords.length > 0) {
    const uniqueKeywords = [...new Set(competitorKeywords)].slice(0, 5);
    const trendResult = await client.getTrendAnalysis(uniqueKeywords, {
      timeframe: '7d',
      platforms: ['twitter', 'reddit', 'tiktok']
    });

    if (trendResult.success) {
      trendData = trendResult.trends;
    }
  }

  // Process and structure the real insights
  const insights = insightsResult.insights;

  const culturalSynthesis = {
    niche,
    cultural_context: {
      target_demographics: extractDemographics(insights.demographics || ''),
      values_alignment: extractValues(insights.keyTakeaways || []),
      current_trends: extractTrends(insights.trends || '', trendData?.emergingThemes || []),
      pain_points: extractPainPoints(insights.opportunities || '')
    },
    market_sentiment: {
      overall_mood: calculateSentiment(insights.keyTakeaways || []),
      trending_themes: trendData?.emergingThemes?.slice(0, 3) || ['authentic expression', 'community connection'],
      seasonal_factors: extractSeasonality(insights.seasonality || ''),
      competitive_landscape: assessCompetitionLevel(competitorData)
    },
    actionable_insights: insights.recommendations || [
      'Focus on authentic emotional messaging that resonates with core values',
      'Leverage current cultural moments and trending topics',
      'Emphasize quality and uniqueness over mass appeal'
    ],
    design_recommendations: {
      style_direction: extractStyleDirection(insights.designInsights || ''),
      messaging_tone: extractMessagingTone(insights.designInsights || ''),
      color_palette: extractColorPalette(insights.designInsights || ''),
      typography: extractTypography(insights.designInsights || ''),
      cultural_hooks: extractCulturalHooks(insights.keyTakeaways || [], niche)
    },
    trend_velocity: trendData?.trendVelocity || 0,
    social_mentions: trendData?.viralContent?.length || 0,
    confidence_score: insights.culturalScore || 75,
    synthesis_timestamp: new Date().toISOString(),
    perplexity_metadata: insightsResult.metadata,
    trend_metadata: trendData ? { keywords: competitorKeywords.slice(0, 5) } : null
  };

  return culturalSynthesis;
}

function extractDemographics(demographicsText) {
  const demo = {
    primary_age: '25-45',
    secondary_age: '18-34',
    gender_split: 'fairly_even',
    income_level: 'middle_to_upper_middle'
  };

  if (demographicsText) {
    // Extract age ranges
    const ageMatch = demographicsText.match(/(\d{2})-(\d{2})/g);
    if (ageMatch && ageMatch[0]) demo.primary_age = ageMatch[0];

    // Extract income indicators
    if (demographicsText.includes('high income') || demographicsText.includes('affluent')) {
      demo.income_level = 'upper_middle_to_high';
    } else if (demographicsText.includes('budget') || demographicsText.includes('lower income')) {
      demo.income_level = 'lower_middle';
    }
  }

  return demo;
}

function extractValues(takeaways) {
  const commonValues = ['authenticity', 'self-expression', 'quality', 'community', 'humor', 'creativity', 'uniqueness'];
  const foundValues = [];

  takeaways.forEach(takeaway => {
    const lowerTakeaway = takeaway.toLowerCase();
    commonValues.forEach(value => {
      if (lowerTakeaway.includes(value) && !foundValues.includes(value)) {
        foundValues.push(value);
      }
    });
  });

  return foundValues.length > 0 ? foundValues : ['Self-expression', 'Authenticity', 'Quality consciousness'];
}

function extractTrends(trendsText, emergingThemes) {
  const trends = [];

  // Extract from Perplexity insights
  if (trendsText) {
    const trendWords = ['minimalist', 'bold', 'vintage', 'sustainable', 'personalized', 'nostalgic'];
    trendWords.forEach(trend => {
      if (trendsText.toLowerCase().includes(trend)) {
        trends.push(`${trend} aesthetic`);
      }
    });
  }

  // Add emerging themes from trend analysis
  emergingThemes.forEach(theme => {
    if (!trends.some(t => t.toLowerCase().includes(theme.toLowerCase()))) {
      trends.push(theme);
    }
  });

  return trends.length > 0 ? trends.slice(0, 5) : ['Minimalist aesthetic', 'Bold statement pieces', 'Authentic messaging'];
}

function extractPainPoints(opportunitiesText) {
  const painPoints = [];

  if (opportunitiesText) {
    if (opportunitiesText.includes('generic') || opportunitiesText.includes('basic')) {
      painPoints.push('Generic mass-market designs');
    }
    if (opportunitiesText.includes('quality') || opportunitiesText.includes('cheap')) {
      painPoints.push('Poor quality prints');
    }
    if (opportunitiesText.includes('size') || opportunitiesText.includes('fit')) {
      painPoints.push('Limited size options');
    }
    if (opportunitiesText.includes('price') || opportunitiesText.includes('expensive')) {
      painPoints.push('Overpriced basic designs');
    }
  }

  return painPoints.length > 0 ? painPoints : ['Generic mass-market designs', 'Poor quality prints', 'Lack of authentic messaging'];
}

function calculateSentiment(takeaways) {
  const positiveWords = ['positive', 'good', 'great', 'excellent', 'amazing', 'love', 'best'];
  const negativeWords = ['negative', 'bad', 'poor', 'worst', 'hate', 'terrible'];

  let positiveCount = 0;
  let negativeCount = 0;

  takeaways.forEach(takeaway => {
    const lowerTakeaway = takeaway.toLowerCase();
    positiveWords.forEach(word => {
      if (lowerTakeaway.includes(word)) positiveCount++;
    });
    negativeWords.forEach(word => {
      if (lowerTakeaway.includes(word)) negativeCount++;
    });
  });

  if (positiveCount > negativeCount) return 'positive';
  if (negativeCount > positiveCount) return 'negative';
  return 'neutral';
}

function extractSeasonality(seasonalityText) {
  const factors = [];

  if (seasonalityText) {
    if (seasonalityText.includes('holiday') || seasonalityText.includes('christmas')) {
      factors.push('Holiday gift-giving season');
    }
    if (seasonalityText.includes('summer') || seasonalityText.includes('vacation')) {
      factors.push('Summer vacation energy');
    }
    if (seasonalityText.includes('back to school') || seasonalityText.includes('september')) {
      factors.push('Back-to-school season');
    }
  }

  return factors.length > 0 ? factors : ['Social media influence peaks', 'Seasonal trend cycles'];
}

function assessCompetitionLevel(competitorData) {
  const competitorCount = competitorData.serp_data?.top_competitors?.length || 0;
  const avgReviews = competitorData.aggregate_insights?.average_reviews || 0;

  if (competitorCount >= 5 && avgReviews > 1000) return 'highly_saturated';
  if (competitorCount >= 3 && avgReviews > 500) return 'moderately_saturated';
  return 'emerging_market';
}

function extractStyleDirection(designInsights) {
  if (!designInsights) return 'clean_minimalist_with_personality';

  const insights = designInsights.toLowerCase();
  if (insights.includes('minimalist') || insights.includes('clean')) return 'clean_minimalist_with_personality';
  if (insights.includes('bold') || insights.includes('statement')) return 'bold_statement_design';
  if (insights.includes('vintage') || insights.includes('retro')) return 'vintage_inspired_aesthetic';

  return 'clean_minimalist_with_personality';
}

function extractMessagingTone(designInsights) {
  if (!designInsights) return 'authentic_relatable_slightly_humorous';

  const insights = designInsights.toLowerCase();
  if (insights.includes('humor') || insights.includes('funny')) return 'humorous_and_engaging';
  if (insights.includes('serious') || insights.includes('professional')) return 'professional_and_trustworthy';
  if (insights.includes('inspirational') || insights.includes('motivational')) return 'inspirational_and_uplifting';

  return 'authentic_relatable_slightly_humorous';
}

function extractColorPalette(designInsights) {
  const palette = ['black', 'white'];

  if (designInsights) {
    const insights = designInsights.toLowerCase();
    if (insights.includes('earth') || insights.includes('natural')) palette.push('earth_tones');
    if (insights.includes('bright') || insights.includes('vibrant')) palette.push('accent_colors');
    if (insights.includes('pastel') || insights.includes('soft')) palette.push('pastel_accents');
  }

  return palette;
}

function extractTypography(designInsights) {
  if (!designInsights) return 'clean_modern_readable';

  const insights = designInsights.toLowerCase();
  if (insights.includes('bold') || insights.includes('strong')) return 'bold_impactful_typography';
  if (insights.includes('script') || insights.includes('handwritten')) return 'script_and_handlettered';

  return 'clean_modern_readable';
}

function extractCulturalHooks(takeaways, niche) {
  const hooks = [`${niche} community insider references`];

  takeaways.forEach(takeaway => {
    if (takeaway.includes('trend') || takeaway.includes('moment')) {
      hooks.push('Trending cultural moments');
    }
    if (takeaway.includes('lifestyle') || takeaway.includes('authentic')) {
      hooks.push('Authentic lifestyle messaging');
    }
  });

  return [...new Set(hooks)];
}

async function main() {
  const inputData = $input.first().json;
  const niche = inputData.niche;

  console.log(`üåç Real cultural synthesis for: ${niche}`);

  try {
    // Track real Perplexity Sonar API cost
    await trackCost('perplexity_synthesis', 0.001, `Agent #07 - Real cultural synthesis for ${niche}`);

    const culturalInsights = await synthesizeCulturalInsights(niche, inputData);

    const result = {
      niche,
      cultural_insights: culturalInsights,
      insight_categories: {
        demographic_insights: culturalInsights.cultural_context.target_demographics,
        trend_analysis: culturalInsights.cultural_context.current_trends,
        opportunity_gaps: culturalInsights.cultural_context.pain_points,
        design_direction: culturalInsights.design_recommendations
      },
      strategic_summary: {
        market_readiness: culturalInsights.confidence_score > 85 ? 'high' : culturalInsights.confidence_score > 70 ? 'medium' : 'low',
        primary_opportunity: culturalInsights.actionable_insights[0],
        design_style: culturalInsights.design_recommendations.style_direction,
        messaging_approach: culturalInsights.design_recommendations.messaging_tone,
        trend_velocity: culturalInsights.trend_velocity,
        social_buzz: culturalInsights.social_mentions > 0 ? 'active' : 'quiet'
      },
      agent_source: 'perplexity-synthesis-07',
      processed_at: new Date().toISOString(),
      real_api: true
    };

    console.log(`‚úÖ Real cultural synthesis complete (confidence: ${culturalInsights.confidence_score}%)`);
    console.log(`üéØ Market readiness: ${result.strategic_summary.market_readiness}`);
    console.log(`üìà Trend velocity: ${culturalInsights.trend_velocity}`);
    console.log(`üí¨ Social mentions: ${culturalInsights.social_mentions}`);

    // Success heartbeat
    await trackCost('system_health', 0.001, 'Agent #07 - Success heartbeat');

    return [{ json: result }];

  } catch (error) {
    console.error('‚ùå Real Perplexity Synthesis error:', error);
    await errorHandler('perplexity-synthesis-07', error, { niche });
    throw error;
  }
}

return await main();
