// ATT System - Niche Generator Agent #16
// Generates 50 keyword combinations from PRD lists for market analysis
// Updated with Google Keyword API integration for real competition data

const GoogleKeywordClient = require('../../clients/google-keyword-client');

// PRD Keyword Lists for Merch by Amazon
const categories = {
  professions: [
    'Teacher', 'Nurse', 'Doctor', 'Engineer', 'Programmer', 'Designer',
    'Chef', 'Firefighter', 'Police', 'Lawyer', 'Accountant', 'Mechanic',
    'Plumber', 'Electrician', 'Photographer', 'Artist', 'Musician', 'Writer'
  ],

  hobbies: [
    'Gaming', 'Fishing', 'Hiking', 'Cooking', 'Gardening', 'Photography',
    'Reading', 'Traveling', 'Yoga', 'Running', 'Cycling', 'Painting',
    'Knitting', 'Woodworking', 'Camping', 'Surfing', 'Skiing', 'Dancing'
  ],

  family: [
    'Mom', 'Dad', 'Grandma', 'Grandpa', 'Sister', 'Brother', 'Son',
    'Daughter', 'Wife', 'Husband', 'Aunt', 'Uncle', 'Cousin', 'Family'
  ],

  attitudes: [
    'Funny', 'Sarcastic', 'Proud', 'Tired', 'Blessed', 'Happy', 'Crazy',
    'Awesome', 'Amazing', 'Strong', 'Smart', 'Cool', 'Wild', 'Free'
  ],

  occasions: [
    'Birthday', 'Christmas', 'Halloween', 'Thanksgiving', 'Valentine',
    'Mother\'s Day', 'Father\'s Day', 'Graduation', 'Retirement', 'Anniversary'
  ],

  trending: [
    'Vintage', 'Retro', 'Minimalist', 'Boho', 'Aesthetic', 'Vibes',
    'Energy', 'Mood', 'Life', 'Goals', 'Squad', 'Tribe', 'Crew', 'Gang'
  ]
};

// Initialize Google Keyword client
const keywordClient = new GoogleKeywordClient();

// Get real competition data using Google Keyword API with Agent #16 ID
async function getCompetitionData(keyword) {
  try {
    // Use Google Keyword API with proper Agent #16 ID for cost tracking
    const searchData = await keywordClient.getSearchVolume(keyword, 'US', 'en', '16');

    // Convert search volume to competition estimate (inverse relationship)
    let competition = 500; // default
    if (searchData.searchVolume) {
      // Higher search volume = higher competition
      competition = Math.min(1000, Math.max(100, Math.round(searchData.searchVolume / 100)));
    }

    console.log(`âœ… Real data for "${keyword}": Volume=${searchData.searchVolume}, Competition=${competition}`);
    return competition;

  } catch (error) {
    console.warn(`âš ï¸ Failed to get real competition data for "${keyword}": ${error.message}`);
    // Fallback to estimated competition based on keyword characteristics
    return estimateCompetition(keyword);
  }
}

// Fallback competition estimation
function estimateCompetition(keyword) {
  const words = keyword.toLowerCase().split(' ');
  let baseCompetition = 300;

  // Adjust based on keyword characteristics
  if (words.includes('mom') || words.includes('dad')) baseCompetition += 200;
  if (words.includes('funny') || words.includes('humor')) baseCompetition += 150;
  if (words.includes('teacher') || words.includes('nurse')) baseCompetition += 100;
  if (words.length > 3) baseCompetition -= 100; // Longer phrases = less competition

  return Math.max(100, Math.min(1000, baseCompetition + Math.floor(Math.random() * 200)));
}

// Generate combinations with market appeal scoring
async function generateNicheCombinations() {
  const combinations = [];
  const usedCombos = new Set();

  // Combination patterns with market appeal weights
  const patterns = [
    { template: '{profession} {attitude}', weight: 0.9 },
    { template: '{hobby} {family}', weight: 0.85 },
    { template: '{attitude} {profession} {family}', weight: 0.88 },
    { template: '{trending} {hobby}', weight: 0.82 },
    { template: '{family} {hobby} {attitude}', weight: 0.86 },
    { template: '{occasion} {profession}', weight: 0.75 },
    { template: '{trending} {profession}', weight: 0.78 },
    { template: '{attitude} {family} {occasion}', weight: 0.73 },
    { template: '{hobby} {trending}', weight: 0.80 },
    { template: '{profession} {occasion}', weight: 0.72 }
  ];

  let attempts = 0;
  while (combinations.length < 50 && attempts < 500) {
    const pattern = patterns[Math.floor(Math.random() * patterns.length)];
    let combo = pattern.template;

    // Replace placeholders with random words
    Object.keys(categories).forEach(category => {
      const placeholder = `{${category}}`;
      if (combo.includes(placeholder)) {
        const words = categories[category];
        const randomWord = words[Math.floor(Math.random() * words.length)];
        combo = combo.replace(placeholder, randomWord);
      }
    });

    // Check for duplicates and add market scoring
    if (!usedCombos.has(combo)) {
      usedCombos.add(combo);

      // Calculate market appeal score
      const wordCount = combo.split(' ').length;
      const popularityBonus = combo.toLowerCase().includes('mom') || 
                             combo.toLowerCase().includes('dad') ? 0.1 : 0;
      const professionalBonus = categories.professions.some(p => 
                               combo.toLowerCase().includes(p.toLowerCase())) ? 0.05 : 0;

      const marketScore = Math.min(95, 
        (pattern.weight * 100) + 
        (wordCount <= 3 ? 5 : -5) + 
        (popularityBonus * 10) + 
        (professionalBonus * 5) + 
        (Math.random() * 10 - 5) // Random variance
      );

      combinations.push({
        niche: combo,
        pattern: pattern.template,
        market_score: Math.round(marketScore),
        word_count: wordCount,
        estimated_competition: await getCompetitionData(combo),
        suggested_price_range: wordCount <= 2 ? '$19.99-$24.99' : '$17.99-$22.99',
        target_audience: determineTargetAudience(combo),
        generated_at: new Date().toISOString(),
        status: 'generated'
      });
    }
    attempts++;
  }

  // Sort by market score descending
  return combinations.sort((a, b) => b.market_score - a.market_score);
}

// Determine target audience based on niche content
function determineTargetAudience(niche) {
  const lower = niche.toLowerCase();

  if (lower.includes('mom') || lower.includes('mother')) return 'mothers';
  if (lower.includes('dad') || lower.includes('father')) return 'fathers';
  if (lower.includes('teacher')) return 'educators';
  if (lower.includes('nurse') || lower.includes('doctor')) return 'healthcare';
  if (lower.includes('gaming') || lower.includes('programmer')) return 'tech_enthusiasts';
  if (lower.includes('funny') || lower.includes('sarcastic')) return 'humor_seekers';
  if (lower.includes('vintage') || lower.includes('retro')) return 'vintage_lovers';

  return 'general_adult';
}

// Main execution
async function main() {
  console.log('ðŸŽ¯ Starting Niche Generator Agent #16 with Google Keyword API...');

  try {
    // Generate niche combinations with real Google Keyword data
    const niches = await generateNicheCombinations();

    console.log(`âœ… Generated ${niches.length} niche combinations using real Google data`);
    console.log(`ðŸ“Š Average market score: ${Math.round(niches.reduce((sum, n) => sum + n.market_score, 0) / niches.length)}`);
    console.log(`ðŸŽ¯ Top 5 niches by market score:`);
    niches.slice(0, 5).forEach((niche, i) => {
      console.log(`   ${i+1}. ${niche.niche} (Score: ${niche.market_score}, Competition: ${niche.estimated_competition})`);
    });

    return niches.map(niche => ({ json: niche }));

  } catch (error) {
    console.error('âŒ Error in Niche Generator Agent #16:', error);
    throw error;
  }
}

// Execute and return results
return await main();

