// ATT System - Niche Analyzer Agent #18
// Calculate opportunity scores and trigger deep analysis

const { trackCost } = require('../../shared/cost-tracker');

function calculateOpportunityScore(competitionData) {
  const metrics = competitionData.metrics;

  // Scoring algorithm (0-100)
  let score = 50; // Base score

  // Competition level scoring (inverse relationship)
  if (metrics.total_results < 1000) score += 30;
  else if (metrics.total_results < 3000) score += 20;
  else if (metrics.total_results < 5000) score += 10;
  else score -= 10;

  // Price point scoring
  const avgPrice = parseFloat(metrics.average_price);
  if (avgPrice > 22) score += 15;
  else if (avgPrice > 19) score += 10;
  else if (avgPrice < 16) score -= 10;

  // BSR scoring (lower is better)
  if (metrics.average_bsr < 50000) score += 20;
  else if (metrics.average_bsr < 100000) score += 10;
  else if (metrics.average_bsr > 500000) score -= 15;

  // Market saturation penalty
  if (metrics.market_saturation === 'high') score -= 15;

  return Math.max(0, Math.min(100, score));
}

async function main() {
  const inputData = $input.first().json;
  const niche = inputData.niche;

  console.log(`ðŸ“Š Analyzing opportunity for: ${niche}`);

  try {
    await trackCost('niche_analysis', 0.001, `Agent #18 - Analyzing ${niche}`);

    const opportunityScore = calculateOpportunityScore(inputData);
    const threshold = parseInt(process.env.OPPORTUNITY_SCORE_THRESHOLD, 10) || 70;
    const isHighPotential = opportunityScore > threshold;

    const analysis = {
      niche,
      opportunity_score: opportunityScore,
      potential_level: isHighPotential ? 'high' : opportunityScore > 50 ? 'medium' : 'low',
      trigger_deep_analysis: isHighPotential,
      analysis_factors: {
        competition_level: inputData.metrics.total_results,
        price_advantage: parseFloat(inputData.metrics.average_price) > 20,
        bsr_opportunity: inputData.metrics.average_bsr > 100000,
        market_saturation: inputData.metrics.market_saturation
      },
      original_data: inputData,
      analyzed_at: new Date().toISOString()
    };

    console.log(`ðŸ“ˆ Opportunity score: ${opportunityScore} (${analysis.potential_level})`);

    return [{ json: analysis }];

  } catch (error) {
    console.error('âŒ Niche Analyzer error:', error);
    await trackCost('niche_analysis', 0.001, `Agent #18 - Error analyzing ${niche}`, true);
    throw error;
  }
}

return await main();
