// Agent #28 Spell Check - Input Processing
// TODO: Implement full processing logic in Phase 6-Œ≤

const { GrammarlyClient } = require('../clients/grammarly-client');
const { validateEnvironment } = require('../shared/utils');

const inputData = $input.first().json;
const designText = inputData.text || inputData.title || '';
const designId = inputData.design_id || 'unknown';

if (!designText) {
  throw new Error('No text content provided for spell checking');
}

// Initialize client
const client = new GrammarlyClient();

// Check environment
const envCheck = validateEnvironment(['GRAMMARLY_API_KEY']);
if (!envCheck.valid && process.env.MOCK_MODE !== 'true') {
  throw new Error('Grammarly API key required unless in mock mode');
}

console.log(`üî§ Agent #28 processing spell check for design: ${designId}`);
console.log(`üìù Text to check: ${designText.substring(0, 100)}...`);

return {
  design_id: designId,
  text_content: designText,
  options: inputData.options || {},
  timestamp: new Date().toISOString(),
  agent: 'spell-check-28'
};

// Agent #28 - Grammarly Spell Check Execution
// TODO: Complete implementation in Phase 6-Œ≤

const { GrammarlyClient } = require('../clients/grammarly-client');
const { errorHandler } = require('../shared/error-handler');

const data = $input.first().json;

try {
  const client = new GrammarlyClient();

  // Perform spell check
  const spellCheckResult = await client.checkText(data.text_content, data.options);

  console.log(`‚úÖ Spell check completed for ${data.design_id}`);
  console.log(`üìä Status: ${spellCheckResult.status}`);
  console.log(`üéØ Grammar Score: ${spellCheckResult.grammar_score}`);
  console.log(`üí° Spelling Errors: ${spellCheckResult.spelling_errors}`);

  return {
    ...data,
    qc_result: spellCheckResult,
    success: true,
    completed_at: new Date().toISOString()
  };

} catch (error) {
  console.error(`‚ùå Spell check failed for ${data.design_id}:`, error.message);

  await errorHandler('spell-check-28', error, {
    design_id: data.design_id,
    text_length: data.text_content?.length || 0
  });

  return {
    ...data,
    qc_result: {
      agent: 'spell-check-28',
      status: 'fail',
      error: error.message,
      processing_time_ms: 0,
      cost: 0
    },
    success: false,
    error: error.message,
    completed_at: new Date().toISOString()
  };
}

// Agent #28 - Error Handling and Logging
// TODO: Enhance error handling in Phase 6-Œ≤

const data = $input.first().json;

console.error(`‚ùå Agent #28 failed for design: ${data.design_id}`);
console.error(`Error: ${data.error}`);

// Log to error tracking system
const errorEntry = {
  agent: 'spell-check-28',
  design_id: data.design_id,
  error: data.error,
  timestamp: new Date().toISOString(),
  retry_count: data.retry_count || 0
};

console.log('Error entry:', JSON.stringify(errorEntry, null, 2));

return {
  ...data,
  error_logged: true,
  error_entry: errorEntry
};

