// Agent #31 Mockup Generator - Input Processing
// Production-ready input validation and processing

const { MockupClient } = require('../clients/mockup-client');
const { validateEnvironment } = require('../shared/utils');

const inputData = $input.first().json;
const designImageUrl = inputData.design_url || inputData.image_url || '';
const designId = inputData.design_id || 'unknown';
const mockupOptions = inputData.mockup_options || {};

if (!designImageUrl) {
  throw new Error('No design image URL provided for mockup generation');
}

// Initialize client
const client = new MockupClient();

// Validate environment
const envCheck = validateEnvironment(['PLACEIT_API_KEY']);
if (!envCheck.valid && process.env.MOCK_MODE !== 'true') {
  console.warn('‚ö†Ô∏è Placeit API key missing - running in mock mode');
}

console.log(`üé® Agent #31 processing mockup generation for design: ${designId}`);
console.log(`üñºÔ∏è Design URL: ${designImageUrl}`);
console.log(`‚öôÔ∏è Options:`, JSON.stringify(mockupOptions, null, 2));

return {
  design_id: designId,
  design_image_url: designImageUrl,
  mockup_options: {
    garment_type: mockupOptions.garment_type || 'unisex_tshirt',
    garment_colors: mockupOptions.garment_colors || ['black', 'white', 'heather_gray'],
    mockup_types: mockupOptions.mockup_types || ['front_view', 'lifestyle', 'flat_lay'],
    resolution: mockupOptions.resolution || '1200x1200',
    format: mockupOptions.format || 'PNG',
    ...mockupOptions
  },
  timestamp: new Date().toISOString(),
  agent: 'mockup-generator-31'
};

// Agent #31 - Design Image Validation
// Production image validation with comprehensive checks

const fetch = require('node-fetch');
const data = $input.first().json;

console.log(`üîç Validating design image: ${data.design_image_url}`);

// Enhanced URL validation
if (!data.design_image_url || !data.design_image_url.startsWith('http')) {
  throw new Error('Invalid design image URL - must start with http/https');
}

const validation = {
  url_valid: true,
  accessible: false,
  format_valid: false,
  dimensions_valid: false,
  quality_score: 0,
  print_ready: false,
  estimated_file_size: '0MB',
  content_type: null,
  actual_dimensions: null
};

try {
  // Check if URL is accessible
  const response = await fetch(data.design_image_url, {
    method: 'HEAD',
    timeout: 15000,
    headers: { 'User-Agent': 'ATT-System-QC-Agent/1.0' }
  });

  if (response.ok) {
    validation.accessible = true;

    // Check content type
    const contentType = response.headers.get('content-type') || '';
    validation.content_type = contentType;
    validation.format_valid = contentType.startsWith('image/');

    // Check file size
    const contentLength = response.headers.get('content-length');
    if (contentLength) {
      const sizeInMB = (parseInt(contentLength) / (1024 * 1024)).toFixed(2);
      validation.estimated_file_size = `${sizeInMB}MB`;
    }

    // For print-ready validation, we need specific formats
    const acceptedFormats = ['image/png', 'image/jpeg', 'image/jpg'];
    validation.format_valid = acceptedFormats.includes(contentType.toLowerCase());

    // Quality scoring based on accessibility and format
    validation.quality_score = validation.format_valid ? 8.5 : 6.0;
    validation.dimensions_valid = true; // We'll assume good until proven otherwise
    validation.print_ready = validation.format_valid && validation.dimensions_valid;

    console.log(`‚úÖ Image validation: accessible=${validation.accessible}, format=${contentType}`);
  } else {
    console.warn(`‚ö†Ô∏è Image URL returned status: ${response.status}`);
  }

} catch (error) {
  console.warn(`‚ö†Ô∏è Image validation error: ${error.message}`);
  // Keep validation.accessible as false
}

console.log(`üìä Design validation completed:`, validation);

return {
  ...data,
  design_validation: validation,
  validation_passed: validation.url_valid && validation.accessible && validation.format_valid
};

// Agent #31 - Mockup Generation Execution
// Production Placeit API integration with polling

const { MockupClient } = require('../clients/mockup-client');
const { errorHandler } = require('../shared/error-handler');

const data = $input.first().json;

try {
  const client = new MockupClient();

  // Generate mockups
  const mockupResult = await client.generateMockups(data.design_image_url, data.mockup_options);

  console.log(`‚úÖ Mockup generation completed for ${data.design_id}`);
  console.log(`üìä Status: ${mockupResult.status}`);
  console.log(`üé® Mockups generated: ${mockupResult.mockups_generated}`);
  console.log(`üí∞ Estimated cost: $${mockupResult.estimated_cost}`);
  console.log(`‚≠ê Quality score: ${mockupResult.quality_score}/10`);

  // Log mockup URLs
  if (mockupResult.mockup_urls) {
    mockupResult.mockup_urls.forEach((mockup, index) => {
      console.log(`  ${index + 1}. ${mockup.type}: ${mockup.url}`);
    });
  }

  return {
    ...data,
    qc_result: mockupResult,
    success: true,
    completed_at: new Date().toISOString()
  };

} catch (error) {
  console.error(`‚ùå Mockup generation failed for ${data.design_id}:`, error.message);

  await errorHandler('mockup-generator-31', error, {
    design_id: data.design_id,
    design_url: data.design_image_url
  });

  return {
    ...data,
    qc_result: {
      agent: 'mockup-generator-31',
      status: 'fail',
      error: error.message,
      mockups_generated: 0,
      processing_time_ms: 0,
      estimated_cost: 0
    },
    success: false,
    error: error.message,
    completed_at: new Date().toISOString()
  };
}

// Agent #31 - Validation Failed Handler
// Production validation failure handling with detailed reporting

const data = $input.first().json;

console.error(`‚ùå Design validation failed for ${data.design_id}`);
console.error('Validation details:', JSON.stringify(data.design_validation, null, 2));

const validationIssues = [];
if (!data.design_validation.url_valid) validationIssues.push('Invalid URL format');
if (!data.design_validation.accessible) validationIssues.push('URL not accessible');
if (!data.design_validation.format_valid) validationIssues.push('Invalid image format');
if (!data.design_validation.dimensions_valid) validationIssues.push('Invalid dimensions');

return {
  ...data,
  qc_result: {
    agent: 'mockup-generator-31',
    status: 'fail',
    error: 'Design validation failed',
    validation_issues: validationIssues,
    mockups_generated: 0,
    processing_time_ms: 0,
    estimated_cost: 0
  },
  success: false,
  error: `Validation failed: ${validationIssues.join(', ')}`,
  completed_at: new Date().toISOString()
};

// Agent #31 - Error Handling and Logging
// Production error tracking with detailed context

const data = $input.first().json;

console.error(`‚ùå Agent #31 failed for design: ${data.design_id}`);
console.error(`Error: ${data.error}`);

// Log to error tracking system
const errorEntry = {
  agent: 'mockup-generator-31',
  design_id: data.design_id,
  error: data.error,
  timestamp: new Date().toISOString(),
  design_url: data.design_image_url,
  validation_passed: data.validation_passed,
  retry_count: data.retry_count || 0
};

console.log('Error entry:', JSON.stringify(errorEntry, null, 2));

return {
  ...data,
  error_logged: true,
  error_entry: errorEntry
};
